//Trying for force an update with new comments!
var gr = new GlideRecord('x_mema_morning_che_jumpbox');
gr.orderByDesc('sys_created_on');
gr.chooseWindow(0,1);
gr.query();
if (gr.next()) {
	var inc = new GlideRecord('incident');
	var comp = gr.company;
	var email = gr.email_address;
	var start = gr.start;
	var sn = gr.loc;
	var name = gr.name;
	var worknote = '[code]<a href="https://' + gs.getProperty('instance_name') + '.service-now.com/nav_to.do?uri=x_mema_morning_che_jumpbox_list.do?sys_id=' + current.sys_id + '">Jumpbox Access Entries</a>[/code]' + '\n Servername: ' + sn + '\n start: ' + start + '\n Name: ' + name + '\n Company: ' + comp + '\n Email: ' + email;
	inc.newRecord();
	inc.initialize();
	inc.caller_id.setDisplayValue('John Reczniarek');
	inc.short_description = "Jumpbox Access: " + sn;
	inc.assignment_group.setDisplayValue('Sys Admin');
	inc.category = "Jumpbox Access";
	inc.urgency = 3;
	inc.priority = 3;
	inc.work_notes = worknote;
	inc.update();
	}

